query IndexQuery {
  allDataJson {
    edges {
      node {
        features {
          title
        }
        howTo {
          title
        }
        recentWork {
          title
          date
          link
          topic
          type
        }
      }
    }
  }
  profileImage: file(relativePath: {eq: "profile-md.jpg"}) {
    childImageSharp {
      sizes(maxWidth: 1000) {
        ...GatsbyImageSharpSizes
      }
    }
  }
}

fragment GatsbyImageSharpSizes on ImageSharpSizes {
  base64
  aspectRatio
  src
  srcSet
  sizes
}
